    pty_name[8] = *ptr;
    pty_name[9] = '0';

    if( stat( pty_name, &statbuff) < 0 )
      break;

    for( i=0; i < 16; i++ ) {
      pty_name[9] = hexdigit[i];
      if( (fd = open( pty_name, O_RDWR )) >= 0 )
	return( fd );
    }
  }
  return(-1);
}



